import styled from 'styled-components'

export const StyledDetailPage = styled.div`
    height: 100vh; 
    display: flex; 
    flex-direction: column; 
    justify-content: center; 
    align-items: center; 
    color: #B2B2B2; 
    

    .firstDiv{
        height: 100px; 
        border-bottom: solid; 

        .h2Div{
            h2{
            font-size: 1rem; 

            }
        }
        .h1Div{

            h1{
                font-size: 2rem; 
            }
        }
    }

    div{
        width: 400px; 
        margin: 20px 0px; 
    }
    td{
        padding-left: 80px; 
    }
`
