import React, {useState, useEffect, useContext} from 'react'
import UserKit from '../data/fetchKit'
import {useHistory} from 'react-router-dom'
import {StyledLogInForm} from '../Style/StyledForms'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCode, faLock, faUser } from '@fortawesome/free-solid-svg-icons'
import { StyledButton, StyledLogInButton } from '../Style/StyledButtons'
import {UserContext} from '../contexts/UserContext'
import Button from './Button'




export default function LoginForm() {
    const history = useHistory()
    const {isLogedIn, setIsLogedIn} = useContext(UserContext)


    useEffect(()=>{
        localStorage.setItem("isLogedIn", false) 
        setIsLogedIn(false)
    }, [])
    
    const [formData, setFormData] = useState({
        email: "kevin.basele@yh.nackademin.se", 
        password: "javascriptoramverk"
    })
    
    const payload = {
        email: formData.email, 
        password: formData.password
    }

    function handleOnChange(e){
        setFormData({...formData, [e.target.name]: e.target.value})
        
    }

    function handleOnSubmit(e){
        e.preventDefault()
        UserKit.loginFetch(payload)
        .then(res => res.json())
        .then(data =>{
            localStorage.setItem("USERTOKEN", data.token)
            localStorage.setItem("isLogedIn", true)
            setIsLogedIn(true)  
            history.push("/homepage");
        })
    }
    
    return (
        <div>
            <StyledLogInForm  onSubmit={handleOnSubmit}>
                <div>
                    <span><FontAwesomeIcon icon={faUser}/></span>
                    <input name="email" onChange={handleOnChange} placeholder={"Email"} value/>
                </div>
                <div>
                <span><FontAwesomeIcon icon={faLock}/></span>   
                <input name="password" onChange={handleOnChange} placeholder={"Password"}/>
                </div>
                <Button login={true} label={"LOG IN"}/>
            </StyledLogInForm>
        </div>
    )
}
