import styled, {css} from 'styled-components'

export const StyledButton = styled.button`
    ${({login}) => login &&
    css`
        background: #9DD1F1; 
        width: 302px; 
        height: 60px; 
        border: none; 
        color: white; 
        font-size: 24px; 
        font-weight: 800; 
        border-radius: 4px; 
        transition: .4s; 
        
        :hover{
            background: #7cc3f0; 
            cursor: pointer;
        }
    `}
    
    ${({logOut}) => logOut &&
    css`
        background: none; 
        border: none; 
        font-size: 1.5rem;

        a{
            text-decoration: none; 
            color: white; 
        }
        
        :hover{
            background: #7cc3f0; 
            cursor: pointer;
        }
    `}

    ${({create}) =>create &&
    css`
        height: 70px; 
        width: 70px;
        border-radius: 100%; 
        background: #9DD1F1; 
        border: none; 
        cursor: pointer;
        color: white; 
        font-size: 1rem; 
        font-weight: 600; 
        margin-top: 20px; 

    `
    }
    

`

export const StyledLogInButton = styled.button`
        background: #9DD1F1; 
        border-radius: 4px; 
        transition: .4s; 
        border: none; 

        ${({login}) => login &&
        css`
            width: 302px; 
            height: 60px; 
            color: white; 
            font-size: 24px; 
            font-weight: 800; 

            :hover{
                background: #7cc3f0; 
                cursor: pointer;
            }
        `}


        width: 302px; 
        height: 60px; 
        color: white; 
        font-size: 24px; 
        font-weight: 800; 
        
        :hover{
            background: #7cc3f0; 
            cursor: pointer;
        }
`
