import styled from 'styled-components'

export const StyledLoginForm = styled.form`
    display: flex; 
    flex-direction: column; 
    align-items: center; 
    color: #B2B2B2;
    
    h1{
        font-size: 4rem;
        font-weight: 900; 
        color: #031927;
    }
    div{
        display: flex; 
        align-items: center; 
        font-size: 2rem;
        background: #F6F6F6; 
        font-weight: 700; 
        width: 496px; 
        height: 85px; 
        border-radius: 135px; 
        margin-bottom: 57px; 

        span{
            margin: 0 40px; 
        }

        input{
            background: #F6F6F6; 
            border: none; 
            font-size: 2rem;
            font-weight: 700; 
            width: 360px;  
        }
    }
`
