import styled, {css} from 'styled-components'

export const StyledButton = styled.button`
    ${({login}) => login &&
    css`
        background: #9DD1F1; 
        width: 302px; 
        height: 60px; 
        border: none; 
        color: white; 
        font-size: 24px; 
        font-weight: 800; 
        border-radius: 4px; 
        transition: .4s; 
        
        :hover{
            background: #7cc3f0; 
            cursor: pointer;
        }
    `}
    
    ${({logOut}) => logOut &&
    css`
        background: none; 
        border: none; 
        font-size: 1.5rem;

        a{
            text-decoration: none; 
            color: white; 
        }
        
        :hover{
            background: #7cc3f0; 
            cursor: pointer;
        }
    `}

    ${({create}) =>create}
    

`
