import styled, {css} from 'styled-components'

export const StyledButton = styled.button`
    


    ${({create}) =>create &&
    css`
        height: 70px; 
        width: 70px;
        border-radius: 100%; 
        background: #9DD1F1; 
        border: none; 
        cursor: pointer;
        color: white; 
        font-size: 1rem; 
        font-weight: 600; 
        margin-top: 20px; 

    `
    }
    

`

export const StyledLogInButton = styled.button`
    border-radius: 4px; 
    transition: .4s; 
    border: none; 
    cursor: pointer;

    a{
    color: white; 
    text-decoration: none; 
    }
    
    ${({login}) => login &&
    css`
        background: #9DD1F1; 
        width: 302px; 
        height: 60px; 
        font-size: 24px; 
        font-weight: 800; 

        :hover{
            background: #7cc3f0; 
        }
    `}
`

export const StyledLogOutButton = styled(StyledLogInButton)`

    ${({logOut}) => logOut &&
    css`
        font-size: 1.2rem; 
        background: none; 
    `
    }
`
export const StyledCreateButton = styled(StyledLogInButton)`
    

    ${({create}) => create &&
    css`

        font-size: 1.2rem; 
        background: blue; 
    `
    }
`
