import {Switch, Route, useHistory} from 'react-router-dom'
import SideBar from './components/SideBar';
import { UserContext } from './contexts/UserContext';
import CreatePage from './pages/CreateCostumerPage';
import CustomerDetailPage from './pages/CustomerDetailPage';
import CustomerUpdatePage from './pages/CustomerUpdatePage';
import HomePage from './pages/HomePage'
import LoginPage from './pages/LoginPage'
import {useState, useEffect} from 'react'
import { StyledContainer } from './Style/StyledContainer';
import LandingPage from './pages/LandingPage';


function App() {
  const [userObj, setUserObj] = useState({})
  const [isToken, setIsToken] = useState(Boolean)
  const history = useHistory()
  const token = localStorage.getItem("USERTOKEN")

  useEffect(()=>{
     if("kevin") history.push()
  })


  return (
    <StyledContainer >
      <UserContext.Provider value={{  userObj, setUserObj, isToken, setIsToken}}>
      { isToken && <SideBar/>}
      <div className="main-content">
        <Switch>
          <Route path="/login">
            <LoginPage/>
          </Route>
          <Route path="/homepage">
            <HomePage/>
          </Route>
          <Route path="/create">
            <CreatePage/>
          </Route>

          <Route path="/customers/:id/edit" component={CustomerUpdatePage}/>
          <Route path="/customers/:id" component={CustomerDetailPage}/>
          <Route path={"/"}>

            <LandingPage/>
          </Route>
        </Switch>

      </div>
      </UserContext.Provider>
    </StyledContainer>
  );
}

export default App;
