import {Link} from 'react-router-dom'
import FetchKit from '../data/fetchKit'

export default function CustomerListItem({customerData}) {

    const [ costumerList, setCostumerList] = useState([])

    function getCostumer(){
        FetchKit.getCostumerFetch()
        .then(res => res.json())
        .then(data => {
            setCostumerList(data.results)
            console.log(costumerList)
        })
    }
    
    useEffect(()=>{
        getCostumer()
    }, [])

    return (
        <div>
            <p>FÃ¶retag</p>
            
            { costumerList.map(item => {
                return (
                <Link to={`/customers/${customerData.id}`}>
                    <p key ={item.id}>{item.name}</p>
                </Link>
                )
            })}
            <button>add company</button>
        </div>
    )
}
