import styled, {css} from 'styled-components'

export const StyledContainer = styled.div`

${({isLogedIn}) => isLogedIn &&
    css`
    display: flex; 
    .main-content{
        background: blue; 
        height: 100vh; 
        width: 82vw; 
    }
    
    ` }
`
