import React, {useState, useEffect, useContext} from 'react'
import {Link, useHistory} from 'react-router-dom'
import FetchKit from '../data/fetchKit'
import Button from './Button'
import {StyledDiv} from '../Style/StyledCustomerListItem'
import {UserContext} from '../contexts/UserContext'



export default function CustomerListItem() {
    const history = useHistory()
    const {setIsToken, customerList, setCustomerList, isToken} = useContext(UserContext)
    const Token = localStorage.getItem("USERTOKEN")

    useEffect(()=>{
        if(localStorage.getItem("USERTOKEN")){
            setIsToken(true)

            FetchKit.getAllCostumersFetch(Token)
            .then(res => res.json())
            .then(data => {
                setCustomerList(data.results)
            }) 
        }
        else{
            history.push("/login")
            console.log("hej")
        }
    },[history, setIsToken, Token])

    return (
        <StyledDiv onM>
            { customerList ? 
                customerList.map(item =>{
                    return (
                    <Link key={item.id} to={`/customers/${item.id}`}>
                        <div>
                            <p> {item.name}</p>
                        </div>
                    </Link>
                    )
                })
            :
                <h1>LOADING</h1>
            }      
            <Button label={"add"} to={"/create"} add={true}/>
        </StyledDiv>
    )
}
