import React, {useState, useEffect, useContext} from 'react'
import FetchKit from '../data/FetchKit'
import {useHistory} from 'react-router-dom'
import Button from '../components/Button'
import {StyledDetailPage} from '../Style/StyledDetailPage'
import { UserContext } from '../contexts/UserContext';

export default function CustomerDetailPage(props) {
    const history = useHistory()

    const {setCustomerList, customerList} = useContext(UserContext) 
    const customerId = props.match.params.id
    const Token = localStorage.getItem("USERTOKEN")
    const [customerData, setCustomerData ] = useState({})


    function deleteCustomerItem(){
        FetchKit.deleteCostumerItemFetch(customerId)
        .then(() => history.push("/homepage"))
    }

    useEffect(()=>{
        if(localStorage.getItem("USERTOKEN")){
            FetchKit.getCostumerItemFetch(customerId)
            .then(res => res.json())
            .then(data => {
                setCustomerData(data)
            })
        }
        else{
            history.push("/login")
        }
    }, [ history])

    function handleOnClick(){
        deleteCustomerItem()
    }

    return (
        
        <StyledDetailPage>
            {customerData ? 
            
