            </div> 
            
            : 
            <h1>loading</h1> 
            }

            <div className={"buttons"}>
                <Button label={"Delete"} del={handleOnClick}/>
                <Button label={"edit"} to={`/customers/${customerId}/edit`} edit={true}/>
            </div>
                <Button label={"back"} to={"/homepage"} back={true}/>
        </StyledDetailPage>
    )
}
