import React, {useState, useEffect} from 'react'
import {useHistory, Link} from 'react-router-dom'
import Button from '../components/Button'
import FetchKit from '../data/fetchKit'


export default function UpdateCustomerForm({props}) {
    const [formData, setFormData] = useState({})
    const history = useHistory()
    const customerId = props.match.params.id

    function getCustomerItem(){
        FetchKit.getCostumerItemFetch(customerId)
        .then(res => res.json())
        .then(data => setFormData(data))
        
    }

    useEffect(()=>{
        getCustomerItem()
    }, [])

    function handleOnChange(e){
        setFormData( {...formData, [e.target.name]: e.target.value})
    }

    function renderInput(name, label, type){
        return (
            <div>
                <label>{label}</label>
                <input 
                    type={type || "text"}
                    name={name} 
                    value={formData[name]}
                    onChange={handleOnChange}
                    
                    />
                <br/>
            </div>
        )
    }
    
    function handleOnSubmit(e){
        e.preventDefault()
        FetchKit.updateCostumerItemFetch(customerId, formData)
        .then(res => res.json())
        .then(data => {
            history.push("/homepage")
        })
        
    }
    
    return (
        <div>
            <form>
                {renderInput("name", "Organisation Name")}
                {renderInput("organisationNr", "Organisation Number")}
                {renderInput("vatNr", "VAT number")}
                {renderInput("reference", "Reference")}
                {renderInput("paymentTerm", "Payment Term")}
                {renderInput("website", "website", "url")}
                {renderInput("email", "Email")}
                {renderInput("phoneNumber", "Phone Number", "tel")}
                <button onClick={handleOnSubmit}>update</button>
                <Link to={`/customers/${customerId}/`}><button>back</button></Link>
            </form>
        </div>
    )
}
