import React, {useState} from 'react'
import UserKit from '../data/fetchKit'
import {useHistory} from 'react-router-dom'
import {StyledLoginForm, styledLoginForm} from '../Style/StyledLoginForm'


export default function LoginForm() {

    const history = useHistory()
    
    const [formData, setFormData] = useState({
        email: "kevin.basele@yh.nackademin.se", 
        password: "javascriptoramverk"
    })
    
    const payload = {
        email: formData.email, 
        password: formData.password
    }

    function handleOnChange(e){
        setFormData({...formData, [e.target.name]: e.target.value})
        
    }

    function handleOnSubmit(e){
        e.preventDefault()
        UserKit.loginFetch(payload)
        .then(res => res.json())
        .then(data =>{
            localStorage.setItem("USERTOKEN", data.token)
            history.push("/homepage");
        })
    }
    
    return (
        <div>
            <StyledLoginForm  onSubmit={handleOnSubmit}>
                <h1>Sign In</h1>
                <input name="email" onChange={handleOnChange} placeholder={"Email"}/>
                <input name="password" onChange={handleOnChange} placeholder={"Password"}/>
                <button type="submit"> log in</button>   
            </StyledLoginForm>

        </div>
    )
}
