import React, {useState} from 'react'
import FetchKit from '../data/fetchKit'
import {useHistory} from 'react-router-dom'
import { StyledForm } from '../Style/StyledForm'
import Button from './Button'


export default function CreateCostumerForm() {
    const [formData, setFormData ]= useState({})
    const history = useHistory()

    function handleOnChange(e){
        setFormData( {...formData, [e.target.name]: e.target.value})
    }

    function renderInput(name, label, type){
        return (
            <StyledForm>
                <div>
                <span></span>
                <input placeholder ={label}
                    type={type || "text"}
                    name={name} 
                    onChange={handleOnChange}/>
                </div>
            </StyledForm>
        )
    }

    function handleOnSubmit(e){
        e.preventDefault()
        console.log("hej")
        FetchKit.createCostumerFetch(formData)
        .then(res => res.json())
        .then(data => {
            history.push("/homepage")
        })
        
    }

    return (
        <form>
            {renderInput("name", "Organisation Name")}
            {renderInput("organisationNr", "Organisation Number")}
            {renderInput("vatNr", "VAT number")}
            {renderInput("reference", "Reference")}
            {renderInput("paymentTerm", "Payment Term")}
            {renderInput("website", "website", "url")}
            {renderInput("email", "Email")}
            {renderInput("phoneNumber", "Phone Number", "tel")}
            <Button label={"create"} create={true} onClick={handleOnSubmit}/>
            <button onClick={handleOnSubmit}>create</button>
        </form>

    )
}
