import React, {useState, useEffect} from 'react'
import FetchKit from '../data/fetchKit'
import {useHistory} from 'react-router-dom'
import Button from '../components/Button'

export default function CustomerDetailPage(props) {
    const history = useHistory()
    const [customerItem, setCustomerItem] = useState(null)
    const customerId = props.match.params.id

    function getCustomerItem(){
        FetchKit.getCostumerItemFetch(customerId)
        .then(res => res.json())
        .then(data => setCustomerItem(data))
        
    }

    function deleteCustomerItem(){
        FetchKit.deleteCostumerItemFetch(customerId)
        .then(() => history.push("/homepage"))
    }

    useEffect(()=>{
        getCustomerItem()
    }, [])

    function handleOnClick(){
        deleteCustomerItem()
        console.log(customerItem)
    }

    return (
        <div>
            {customerItem ? 
            <div>
                <h1>{customerItem.name}</h1>
                <h1>{customerItem.email}</h1>
            </div>
            
            : <h1>loading</h1> }
            
            <Button label={"Delete"} onClick={handleOnClick}/>
            <Button label={"back"} path={"/homepage"}/>
        </div>
    )
}
