import React, {useState} from 'react'
import UserKit from '../data/fetchKit'
import {useHistory} from 'react-router-dom'
import {StyledLoginForm, styledLoginForm} from '../Style/StyledLoginForm'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCode, faLock, faUser } from '@fortawesome/free-solid-svg-icons'
import Button from './Button'


export default function LoginForm() {

    const history = useHistory()
    
    const [formData, setFormData] = useState({
        email: "kevin.basele@yh.nackademin.se", 
        password: "javascriptoramverk"
    })
    
    const payload = {
        email: formData.email, 
        password: formData.password
    }

    function handleOnChange(e){
        setFormData({...formData, [e.target.name]: e.target.value})
        
    }

    function handleOnSubmit(e){
        e.preventDefault()
        UserKit.loginFetch(payload)
        .then(res => res.json())
        .then(data =>{
            localStorage.setItem("USERTOKEN", data.token)
            history.push("/homepage");
        })
    }
    
    return (
            <StyledLoginForm  onSubmit={handleOnSubmit}>
                <h1>Sign In</h1>
                <div>
                    <span><FontAwesomeIcon icon={faUser}/></span>
                    <input name="email" onChange={handleOnChange} placeholder={"Email"}/>
                </div>
                <div>
                <span><FontAwesomeIcon icon={faLock}/></span>

                <input name="password" onChange={handleOnChange} placeholder={"Password"}/>
                </div>
                <Button type="submit"> log in</Button>   
            </StyledLoginForm>
    )
}
