import React, {useState, useEffect, useContext} from 'react'
import FetchKit from '../data/FetchKit'
import {useHistory} from 'react-router-dom'
import Button from '../components/Button'
import {StyledDetailPage} from '../Style/StyledDetailPage'
import {UserContext} from '../contexts/UserContext'


export default function CustomerDetailPage(props) {
    const history = useHistory()

    const customerId = props.match.params.id
    const [customerData, setCustomerData ] = useState({})
    const { setIsUpToDatet, customerList} = useContext(UserContext)



    function deleteCustomerItem(){
        FetchKit.deleteCostumerItemFetch(customerId)
        .then(() => history.push("/homepage"))
    }

/*     function findCurr(){
        for(let curr of customerList){
            if(customerId == curr.id){
                console.log(customerList.curr.id[customerId])
                console.log(customerList.indexOf(curr.id[customerId]))
             }
        }
        console.log(customerList)
    } */
    function findCurr(){
        let index = customerList.findIndex(element =>{
            if(element.id == customerId ){
                console.log(customerId)
            } return true
        })
        console.log(index)
        console.log(customerList)
    } 

    useEffect(()=>{
        

        if(localStorage.getItem("USERTOKEN")){

            findCurr()
            FetchKit.getCostumerItemFetch(customerId)
            .then(res => res.json())
            .then(data => {
                setCustomerData(data)
            })
        }
        else{
            history.push("/login")
        }
    },[])

    function handleOnClick(){
        deleteCustomerItem()
    }

    return (
        
        <StyledDetailPage>
            {customerData ? 
            
            <div>
                {Object.entries(customerData).map((item, index) =>{
                    if(item[0] !== "id" && item[0] !== "parent" && item[0] !== "address"){
                        return ( 
                                <div key={index}  className={"firstDiv"}>
                                    <div className="h2Div">
                                        <h2>{item[0]}</h2>
                                    </div>
                                    <div className="h1Div">
                                        <h1>{item[1]}</h1>
                                    </div>
                                </div>
                            )
                        }
                        else return ""
                    })}
                    
            </div> 

            
            : 
            <h1>loading</h1> 
            }

            <div className={"buttons"}>
                <Button label={"Delete"} del={handleOnClick}/>
                <Button label={"edit"} edit={true} to={`/customers/${customerId}/edit`} />
            </div>
                <Button label={"back"} to={"/homepage"} back={true}/>
        </StyledDetailPage>
    )
}
