import React, { useEffect, useContext} from 'react'
import {Link, useHistory} from 'react-router-dom'
import FetchKit from '../data/FetchKit'
import Button from './Button'
import {StyledDiv} from '../Style/StyledCustomerListItem'
import {UserContext} from '../contexts/UserContext'



export default function CustomerListItem() {
    const history = useHistory()
    const { customerList, setAuth, setCustomerList} = useContext(UserContext)
    const token = localStorage.getItem("USERTOKEN")
    

    useEffect(()=>{
        if(token){
            setAuth(true)
            FetchKit.getAllCostumersFetch(token)
            .then(res => res.json())
            .then(data => {
                setCustomerList(data.results)
            },[])              
        }

        else{
            history.push("/login")
        }
    },[token, customerList, history, setAuth, setCustomerList])

    return (
        <StyledDiv >
            { customerList ? 
                customerList.map(item =>{
                    return (
                    <Link onMouseMove={()=>console.log(customerList)} key={item.id} to={`/customers/${item.id}`}>
                        <div>
                            <p> {item.name}</p>
                        </div>
                    </Link>
                    )
                })
            :
                <h1>LOADING</h1>
            }      
            <Button label={"add"} to={"/create"} add={true}/>
        </StyledDiv>
    )
}
