        <h1>Companies</h1>
        
        { costumerList.map(item => {
            return (
                    <Link key={item.id} to={`/customers/${item.id}`}>
                        <div>
                            <p> {item.name}</p>
                        </div>
                    </Link>
            )
        })}
        
        <Button label={"add"} to={"/create"} add={true}/>
    </StyledDiv> */
